package while_loop;

public class Add_3_single_line {
	void print()
	{
	 int no=1;
	int add_number=3;
	// int number_of_no=0;
	 
	  while(no<=40)
	  {
		  System.out.print(" "+no);
		 		  
		  no=no+add_number;
	  }   
						
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Add_3_single_line number=new Add_3_single_line();
		number.print();

	}

}
